mapper.select:
select

from hty_fct_member_org_detail


    and ID = #{id}


    and date_key = #{dateKey}


    and org_code = #{orgCode}


    and cust_code = #{custCode}


    and xs_amt = #{xsAmt}


    and xs_qty = #{xsQty}


    and last_date = #{lastDate}

 select

  from hty_fct_member_org_detail
	where  date_key >= #{startTime} and date_key <= #{endTime} and cust_code = #{custCode}
